/*****************************************
* DEPENDENCIES
*****************************************/

// Initialize modules
// Importing specific gulp API functions lets us write them below as series() instead of gulp.series()
const { src, dest, watch, series, parallel } = require('gulp');
// Importing all the Gulp-related packages we want to use
const babelify = require('babelify');
const browserify = require('browserify');
const changed = require('gulp-changed')
const colors = require('colors');
const imagemin = require('gulp-imagemin');
const sourcemaps = require('gulp-sourcemaps');
const sass = require('gulp-sass');
const uglify = require('gulp-uglify');
const postcss = require('gulp-postcss');
const autoprefixer = require('autoprefixer');
const cssnano = require('cssnano');
const source = require('vinyl-source-stream');
const buffer = require('vinyl-buffer');
const plumber = require('gulp-plumber');
const notify = require('gulp-notify');

const fs = require('fs');
const replace = require('gulp-replace');


/*****************************************
* CONFIGURATION
*****************************************/

// Import our per-project configurations
let config;
try {
	config = require('./statickit.json');
} catch(err) {}

// A dictionary of various directories
const directories = {
	dist : {
		css : './css/',
		images : './images/',
		js : './js/',
	},
	src : {
		images : 'images/src/',
		js : 'js/src/',
		scss : 'css/src/'
	}
};

// Allow for overwrites
if(config && config.paths) {
	if(config.paths.dist) {
		for(var i in config.paths.dist) {
			if(directories.dist[i]) {
				directories.dist[i] = config.paths.dist[i];
			}
		}
	}
	if(config.paths.src) {
		for(var i in config.paths.src) {
			if(directories.src[i]) {
				directories.src[i] = config.paths.src[i];
			}
		}
	}
}

// A dictionary of important files
const files = {
	src : {
		js : directories.src.js + 'scripts.js',
		scss : directories.src.scss + 'styles.scss'
	}
}



/*****************************************
* NOTIFICATIONS / LOGGING
*****************************************/

// We don't need to see the gulp-notify in the logs
notify.logLevel(0);

// Colorize errors
colors.setTheme({
	error: ['bold','red']
});

// Log errors to console
function _error(msg) {
	console.log('‚ò†Ô∏è  ' + msg.error);
}

// An icon for gulp-notify notifications (just for a nice touch)
const icon = 'data:image/png;base64,';

function showErrorNotification(err) {
	_error(err.formatted);
    notify.onError({
        title: 'üö® Oh no!',
        message:  'Bad things happened. Check your command line for details.',
    	icon: icon    
    })(err);
}



/*****************************************
* GULP TASKS
*****************************************/

// Image minification task: optimizes images for web and saves as a separate file
function imageMinTask() {
	return src(directories.src.images + '*')
        .pipe(plumber({ errorHandler: showErrorNotification}))
		.pipe(changed(directories.dist.images))
		.pipe(imagemin())
		.pipe(dest(directories.dist.images))
	    .pipe(notify({
	        title: 'üëç Order up!',
	        message:  'Images successfully optimized',
	    	icon: icon,
	    	onLast: true    
	    }));
}

// JS task: concatenates and uglifies JS files to script.js
function jsTask() {

	let bundler = browserify(files.src.js).transform('babelify', {presets: ['@babel/preset-env']});

    return bundler.bundle()
        .pipe(plumber({ errorHandler: showErrorNotification}))
	    .pipe(source('scripts.js'))
	    .pipe(buffer())
	    .pipe(sourcemaps.init())
	    .pipe(uglify())
	    .pipe(sourcemaps.write('./'))
	    .pipe(dest(directories.dist.js))
	    .pipe(notify({
	        title: 'üëç Order up!',
	        message:  'Javascript successfully compiled',
	    	icon: icon,
	    	onLast: true    
	    }));
}

// Sass task: compiles the styles.scss file into styles.css
function scssTask() {
    return src(files.src.scss)
        .pipe(plumber({ errorHandler: showErrorNotification}))
        .pipe(sourcemaps.init()) // initialize sourcemaps first
        .pipe(sass({
			includePaths: ['node_modules']
		})) // compile SCSS to CSS
        .pipe(postcss([ autoprefixer(), cssnano() ])) // PostCSS plugins
        .pipe(sourcemaps.write('.')) // write sourcemaps file in current directory
        .pipe(dest(directories.dist.css)) // put final CSS in dist folder
	    .pipe(notify({
	        title: 'üëç Order up!',
	        message:  'Scss successfully compiled',
	    	icon: icon,
	    	onLast: true    
	    }));
}


// Sass task: compiles the styles.scss file into styles.css
function importCssTask() {
    return src('../report.template.html')
		.pipe(replace(/<style((.|\n|\r)*?)<\/style>/, function(s) {
			var style = fs.readFileSync('css/styles.css', 'utf8');
			return '<style>\n' + style + '</style>';
		}))
		.pipe(dest('../'));
}


// Sass task: compiles the scripts.js file into scripts.js
function importJsTask() {
    return src('../report.template.html')
		.pipe(replace(/<script((.|\n|\r)*?)<\/script>/, function(s) {
			var script = fs.readFileSync('js/scripts.js', 'utf8');
			return '<script>\n' + script + '</script>';
		}))
		.pipe(dest('../'));
}

// Watch task: watch SCSS and JS files for changes
// If any change, run scss and js tasks simultaneously
function watchTask() {
    watch([
	    	directories.src.images + '**/*.*', 
	    	directories.src.scss + '**/*.scss', 
	    	directories.src.js + '**/*.js'
    	],
        {interval: 1000, usePolling: true}, //Makes docker work
        series(
            parallel(imageMinTask, scssTask, jsTask),
            importCssTask,
            importJsTask
        )
    );    
}

// Export the default Gulp task so it can be run
// Runs the images, scss and js tasks simultaneously
// then watch task
exports.default = series(
    parallel(imageMinTask, scssTask, jsTask),
    importCssTask,
    importJsTask,
    watchTask
);
